import{_ as n,c as s,a2 as t,o as e}from"./chunks/framework.CJ3Fe3Yg.js";const i="/assets/%E5%9B%BE%E7%89%87.D9PHBJIi.png",g=JSON.parse('{"title":"詞庫維護","description":"","frontmatter":{},"headers":[],"relativePath":"moran-docs/02.安装与维护/词库维护.md","filePath":"moran-docs/02.安装与维护/词库维护.md"}'),l={name:"moran-docs/02.安装与维护/词库维护.md"};function o(r,a,p,d,c,m){return e(),s("div",null,a[0]||(a[0]=[t(`<h1 id="詞庫維護" tabindex="-1">詞庫維護 <a class="header-anchor" href="#詞庫維護" aria-label="Permalink to &quot;詞庫維護&quot;">​</a></h1><h2 id="如何造詞" tabindex="-1">如何造詞？ <a class="header-anchor" href="#如何造詞" aria-label="Permalink to &quot;如何造詞？&quot;">​</a></h2><p>類整句模式（默認模式、整句模式、輔篩模式）下，任何連續輸入的文本都會被添加到<strong>用戶詞庫</strong>中。</p><p>字詞模式下，默認情況下只能打詞庫和用戶詞庫中有的詞。要添加新詞到<strong>用戶詞庫</strong>中，可以使用<a href="./../07.其他模式/00.魔然字词方案/特性说明.html#手動造詞鍵">單引號快捷加詞</a>。</p><p>類整句模式的用戶詞庫保存在用戶目錄下的 moran.userdb 中。該目錄是 LevelDB 數據庫文件，不能直接編輯修改，但可以用 Rime 提供的詞庫管理程序導出或導入 txt。</p><p>字詞模式的用戶詞庫保存在用戶目錄下的 moran_fixed.txt 或 moran_fixed_simp.txt 中，可以直接使用文本編輯器查看和修改。</p><h2 id="如何把詞語寫入詞庫文件" tabindex="-1">如何把詞語寫入詞庫文件？ <a class="header-anchor" href="#如何把詞語寫入詞庫文件" aria-label="Permalink to &quot;如何把詞語寫入詞庫文件？&quot;">​</a></h2><p><strong>字詞模式</strong>：編輯 moran_fixed.dict.yaml（傳承字碼表）或 moran_fixed_simp.dict.yaml（簡化字碼表）。</p><p><strong>整句模式</strong>：編輯 moran.extended.dict.yaml，</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># 用戶自定義詞庫</span></span>
<span class="line"><span># ^ 在此提示之後，按 &lt;詞&gt; &lt;tab&gt; &lt;編碼&gt; 的格式一行一個詞。</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 可以不加編碼，Rime 會自動生成編碼</span></span>
<span class="line"><span>魔然說明書</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 編碼可以任取，甚至字數和音節數不匹配也可以</span></span>
<span class="line"><span># 通過這種方式可以使得整句中可以混入「簡碼」連打</span></span>
<span class="line"><span>什麼	sm</span></span>
<span class="line"><span>爲什麼	wsm</span></span></code></pre></div><h2 id="如何導入其他詞庫文件" tabindex="-1">如何導入其他詞庫文件？ <a class="header-anchor" href="#如何導入其他詞庫文件" aria-label="Permalink to &quot;如何導入其他詞庫文件？&quot;">​</a></h2><p>對於類整句方案（默認模式、整句模式、輔篩模式），編輯 moran.extended.dict.yaml，在 <code>import_tables</code> 下輸入自己的詞庫名。</p><p>詞庫需是原生雙拼詞庫。具體格式可參考 moran.base.dict.yaml。</p><p><strong>詞語編碼可以沒有輔碼</strong>，但這樣的詞語在默認模式和整句模式中不能加輔（輔篩模式不受影響）。</p><p>我們提供<a href="./../06.工具/方案生成工具/README.html#命令-gen-dict">詞庫格式轉換工具</a>。</p><p>作爲一個用例，您可以把字詞模式詞庫完全導入到整句詞庫中（雖然不建議這樣的用法）：</p><figure><img src="`+i+'" alt=""><figcaption></figcaption></figure><h2 id="附錄-有哪些詞庫文件" tabindex="-1">附錄：有哪些詞庫文件？ <a class="header-anchor" href="#附錄-有哪些詞庫文件" aria-label="Permalink to &quot;附錄：有哪些詞庫文件？&quot;">​</a></h2><p>詞庫文件分爲兩類：</p><ul><li>整句詞庫，由類整句方案使用，包括<strong>默認模式</strong>、<strong>整句模式</strong>、<strong>輔篩模式</strong><ul><li>上述方案直接引用 moran.extended.dict.yaml，該文件中導入以下詞庫 <ul><li>moran.chars.dict.yaml 單字全碼定義</li><li>moran.base.dict.yaml 核心詞庫</li><li>moran.words.dict.yaml 本方案擴充詞庫</li><li>moran.tencent.dict.yaml 騰訊詞向量詞庫</li><li>moran.computer.dict.yaml 計算機術語詞庫</li><li>moran.moe.dict.yaml 萌娘百科詞頭</li><li>moran.hanyu.dict.yaml 漢語大詞典（默認未開啓）</li></ul></li></ul></li><li>字詞和簡碼詞庫，由<strong>字詞模式</strong>和<strong>默認模式</strong>使用 <ul><li>moran_fixed.dict.yaml 傳承字碼表</li><li>moran_fixed_simp.dict.yaml 簡化字碼表</li></ul></li></ul>',20)]))}const u=n(l,[["render",o]]);export{g as __pageData,u as default};
