import{_ as d,a as r}from"./chunks/image (90).BJIYCQxa.js";import{_ as a,c as o,a4 as s,o as i}from"./chunks/framework.NOSrbWr_.js";const b=JSON.parse('{"title":"自然碼形輔","description":"","frontmatter":{"order":2,"title":"自然碼形輔"},"headers":[],"relativePath":"book/basics/zrmxf.md","filePath":"book/basics/zrmxf.md"}'),l={name:"book/basics/zrmxf.md"};function e(n,t,p,c,g,h){return i(),o("div",null,t[0]||(t[0]=[s('<h1 id="自然碼形輔" tabindex="-1">自然碼形輔 <a class="header-anchor" href="#自然碼形輔" aria-label="Permalink to &quot;自然碼形輔&quot;">​</a></h1><h2 id="取碼規則" tabindex="-1">取碼規則 <a class="header-anchor" href="#取碼規則" aria-label="Permalink to &quot;取碼規則&quot;">​</a></h2><p>只需記住如下規則，就掌握了自然碼形輔的 80%：</p><ul><li><strong>第一個字母</strong> 取這個字的 <strong>部首</strong> 的雙拼首字母。</li><li><strong>第二個字母</strong> 取這個字 <strong>除部首外的最大可識讀部件</strong> 的雙拼首字母。</li></ul><p>例如：「好」字，部首是「女」（n），去掉部首剩下「子」（z），合併起來，就是 <code>nz</code>。</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>特殊情況1：字根字，如「衣」「魚」等，有兩種打法：</p><ol><li>使用首末筆畫，如「衣」首末筆分別是點捺兩筆，對應輔助碼 dd。</li><li>依據正常拆分規則拆字，如「魚」可拆爲「刀灬」，對應輔助碼 dh。</li></ol><p>字根字一般都會有這兩種打法，所以一般只需按正常拆字規則拆字即可。如果個別字按第二種打法打不出（如「衣」），再嘗試第一種。</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>特殊情況2：單筆畫字的第二位輔助碼重複第一位輔助碼。</p><p>如「一」、「〇」、「の」的輔助碼都是 aa。</p><p>（是的，「の」不是漢字，但是因爲比較常用，所以給了一個編碼 noa，其中 no 是「の」的羅馬字拼寫。）</p></div><h2 id="鍵位圖和需要特別記憶的字根" tabindex="-1">鍵位圖和需要特別記憶的字根 <a class="header-anchor" href="#鍵位圖和需要特別記憶的字根" aria-label="Permalink to &quot;鍵位圖和需要特別記憶的字根&quot;">​</a></h2><p>自然碼是「見字識根」類輸入方案，因此您<strong>無需特意記憶字根</strong>（實際上也是記不完的，根本不存在一個窮舉所有字根的列表）。只要在打字時根據規則取碼即可。下圖僅作爲參考：</p><figure><img src="'+d+'" alt=""><figcaption><p>自然碼輔助碼鍵位圖（製圖：更漏子）</p></figcaption></figure><p>大部分字根歸類到其聲母鍵位上，只有少量字根需要特別注意（按重要程度排序）：</p><table tabindex="0"><thead><tr><th>編碼</th><th>部件</th><th>助記</th></tr></thead><tbody><tr><td>f</td><td>扌</td><td><strong>扶</strong>手旁</td></tr><tr><td>d</td><td>丶、冫、氵、捺筆</td><td><strong>點</strong></td></tr><tr><td>o</td><td>日、月、目、曰</td><td>象形+歸併</td></tr><tr><td>a</td><td><p>一、丨、乛、亅<br>（所有橫、豎、折、彎筆）</p></td><td></td></tr><tr><td>p</td><td><p>丿<br>（所有丿）</p></td><td>撇</td></tr><tr><td>g</td><td><p>乚、勹<br>（所有勾）</p></td><td><strong>勾</strong></td></tr><tr><td>w</td><td>亠、夂、夊、攵</td><td><strong>文</strong>字頭</td></tr><tr><td>b</td><td>宀、冖</td><td><strong>寶</strong>蓋頭、禿<strong>寶</strong>蓋</td></tr><tr><td>q</td><td>䒑</td><td><strong>前</strong>字頭</td></tr><tr><td>i</td><td>厂</td><td>「<strong>廠</strong>」的簡化字</td></tr><tr><td>g</td><td>广</td><td>「<strong>廣</strong>」的簡化字</td></tr><tr><td>y</td><td>业</td><td>「<strong>業</strong>」的簡化字</td></tr><tr><td>y</td><td>乁㇈</td><td>「<strong>乙</strong>」</td></tr></tbody></table><details class="details custom-block"><summary>練習：給出這些字的輔助碼：飞、一、〇、廠、𠆭、𡆢</summary><ul><li>飞：yd 乙丶</li><li>一：aa 一一</li><li>〇：aa （曲筆）</li><li>廠：gi 广敞</li><li>𠆭：ra（曲筆）</li><li>𡆢：ka（曲筆）</li></ul></details><h2 id="字根編碼" tabindex="-1">字根編碼 <a class="header-anchor" href="#字根編碼" aria-label="Permalink to &quot;字根編碼&quot;">​</a></h2><p>可以使用 <code>pp</code> + 字根編碼輸入字根。</p><figure><img src="'+r+'" alt=""><figcaption></figcaption></figure><div class="info custom-block"><p class="custom-block-title">INFO</p><p>目前僅在<a href="./../usage/">默認模式</a>和<a href="./../schemas/zhengju/">整句模式</a>中可用。</p></div><h2 id="自然碼形輔特點" tabindex="-1">自然碼形輔特點 <a class="header-anchor" href="#自然碼形輔特點" aria-label="Permalink to &quot;自然碼形輔特點&quot;">​</a></h2><ul><li>學習成本極低</li><li>依據漢字本身的信息編碼，作者主觀干擾極少</li><li>傳承字和簡化字前三碼往往一致，在不同字形間切換幾乎沒有成本</li></ul>',19)]))}const f=a(l,[["render",e]]);export{b as __pageData,f as default};
