import{_ as a,a as i,b as d,c as r,d as l,e,f as s,g as o,h,i as m}from"./chunks/image (82).oKHeAd98.js";import{_ as n,c,a4 as p,o as u}from"./chunks/framework.NOSrbWr_.js";const y=JSON.parse('{"title":"輸入方法","description":"","frontmatter":{"title":"輸入方法","order":2},"headers":[],"relativePath":"book/schemas/fushai/usage.md","filePath":"book/schemas/fushai/usage.md"}'),_={name:"book/schemas/fushai/usage.md"};function g(b,t,f,z,k,x){return u(),c("div",null,t[0]||(t[0]=[p('<h1 id="輸入方法" tabindex="-1">輸入方法 <a class="header-anchor" href="#輸入方法" aria-label="Permalink to &quot;輸入方法&quot;">​</a></h1><h2 id="單字輸入方法" tabindex="-1">單字輸入方法 <a class="header-anchor" href="#單字輸入方法" aria-label="Permalink to &quot;單字輸入方法&quot;">​</a></h2><p>輸入單字雙拼後，直接選擇候選項，或輸入 1 位或 2 位輔助碼。</p><ul><li>輸入長度爲 3 時，優先輸出單字候選項。</li><li>輸入長度爲 4 時，單字首選項會與一起被調頻。 <ul><li>例如，輸入 doxx 時，若輸入「多謝」次數比較多，則它可能出現在「多」的前面。</li></ul></li></ul><table data-view="cards"><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td></td><td><img src="'+a+'" alt="" data-size="original"></td><td>無輔助碼</td></tr><tr><td></td><td><img src="'+i+'" alt="" data-size="original"></td><td>1 位輔助碼</td></tr><tr><td></td><td><img src="'+d+'" alt="" data-size="original"></td><td>2 位輔助碼</td></tr></tbody></table><h2 id="詞語輸入方法" tabindex="-1">詞語輸入方法 <a class="header-anchor" href="#詞語輸入方法" aria-label="Permalink to &quot;詞語輸入方法&quot;">​</a></h2><p>輸入詞語雙拼後，可直接選擇候選項，或輸入 1 位或 2 位輔助碼。</p><p>詞語輔助碼全碼的定義是：</p><ol><li>首字的 2 位輔助碼</li><li>末字的 2 位輔助碼</li><li>末字的 1 位輔助碼 + 首字的 1 位輔助碼</li><li>首字的 1 位輔助碼 + 末字的 1 位輔助碼</li></ol><table data-view="cards"><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td><img src="'+r+'" alt="" data-size="original"></td><td></td><td>首字 2 碼</td></tr><tr><td><img src="'+l+'" alt="" data-size="original"></td><td></td><td>末字 2 碼</td></tr><tr><td><img src="'+e+'" alt="" data-size="original"></td><td></td><td>末 1 + 首 1</td></tr><tr><td><img src="'+s+'" alt="" data-size="original"></td><td></td><td>首 1 + 末 1</td></tr></tbody></table><h2 id="句子輸入方法與造詞方法" tabindex="-1">句子輸入方法與造詞方法 <a class="header-anchor" href="#句子輸入方法與造詞方法" aria-label="Permalink to &quot;句子輸入方法與造詞方法&quot;">​</a></h2><p>輸入句子的完整全拼後，檢視首選，如果首選不是想要輸入的句子，則可輸入 <strong>第一個詞語或單字的</strong> 1 位 或 2 位輔助碼。在輸入完成後，該詞語會進入用戶詞庫，並在「魔然」和「魔然·整句」中也可以使用。</p><p>以輸入和造詞「輔篩模式」爲例：</p><table data-view="cards"><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td><img src="'+o+'" alt="" data-size="original"></td><td>輸入雙拼</td><td></td></tr><tr><td><img src="'+h+'" alt="" data-size="original"></td><td></td><td>輸入「輔」的輔助碼 i</td></tr><tr><td><img src="'+m+'" alt="" data-size="original"></td><td>輸入「篩」的輔助碼 v</td><td></td></tr></tbody></table><ul><li>一開始直接輸入「輔篩模式」的雙拼 fu ul mo ui，觀察到首選不是想要的詞語。</li><li>輸入 i ，即「輔」的輔助碼，觀察到「輔」字被提升到次選項，輸入 2 選擇該字。</li><li>輸入 v，即「篩」的輔助碼，觀察到「篩」字被提升到次選項，輸入 2 選擇該字。</li><li>觀察到首選是「模式」，空格確認。</li><li>上屏「輔篩模式」。</li><li>再次輸入 fu ul mo ui，觀察到首選是「輔篩模式」，說明該詞語已經進入用戶詞庫。</li></ul>',15)]))}const P=n(_,[["render",g]]);export{y as __pageData,P as default};
