import{_ as d,a as r}from"./chunks/image (90).BJIYCQxa.js";import{_ as a,c as o,a4 as s,o as i}from"./chunks/framework.NOSrbWr_.js";const b=JSON.parse('{"title":"自然码形辅","description":"","frontmatter":{"order":2,"title":"自然码形辅"},"headers":[],"relativePath":"zh-Hans/book/basics/zrmxf.md","filePath":"zh-Hans/book/basics/zrmxf.md"}'),l={name:"zh-Hans/book/basics/zrmxf.md"};function e(n,t,p,c,g,h){return i(),o("div",null,t[0]||(t[0]=[s('<h1 id="自然码形辅" tabindex="-1">自然码形辅 <a class="header-anchor" href="#自然码形辅" aria-label="Permalink to &quot;自然码形辅&quot;">​</a></h1><h2 id="取码规则" tabindex="-1">取码规则 <a class="header-anchor" href="#取码规则" aria-label="Permalink to &quot;取码规则&quot;">​</a></h2><p>只需记住如下规则，就掌握了自然码形辅的 80%：</p><ul><li><strong>第一个字母</strong> 取这个字的 <strong>部首</strong> 的双拼首字母。</li><li><strong>第二个字母</strong> 取这个字 <strong>除部首外的最大可识读部件</strong> 的双拼首字母。</li></ul><p>例如：「好」字，部首是「女」（n），去掉部首剩下「子」（z），合并起来，就是 <code>nz</code>。</p><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>特殊情况1：字根字，如「衣」「鱼」等，有两种打法：</p><ol><li>使用首末笔画，如「衣」首末笔分别是点捺两笔，对应辅助码 dd。</li><li>依据正常拆分规则拆字，如「鱼」可拆为「刀灬」，对应辅助码 dh。</li></ol><p>字根字一般都会有这两种打法，所以一般只需按正常拆字规则拆字即可。如果个别字按第二种打法打不出（如「衣」），再尝试第一种。</p></div><div class="tip custom-block"><p class="custom-block-title">TIP</p><p>特殊情况2：单笔画字的第二位辅助码重复第一位辅助码。</p><p>如「一」、「〇」、「の」的辅助码都是 aa。</p><p>（是的，「の」不是汉字，但是因为比较常用，所以给了一个编码 noa，其中 no 是「の」的罗马字拼写。）</p></div><h2 id="键位图和需要特别记忆的字根" tabindex="-1">键位图和需要特别记忆的字根 <a class="header-anchor" href="#键位图和需要特别记忆的字根" aria-label="Permalink to &quot;键位图和需要特别记忆的字根&quot;">​</a></h2><p>自然码是「见字识根」类输入方案，因此您<strong>无需特意记忆字根</strong>（实际上也是记不完的，根本不存在一个穷举所有字根的列表）。只要在打字时根据规则取码即可。下图仅作为参考：</p><figure><img src="'+d+'" alt=""><figcaption><p>自然码辅助码键位图（制图：更漏子）</p></figcaption></figure><p>大部分字根归类到其声母键位上，只有少量字根需要特别注意（按重要程度排序）：</p><table tabindex="0"><thead><tr><th>编码</th><th>部件</th><th>助记</th></tr></thead><tbody><tr><td>f</td><td>扌</td><td><strong>扶</strong>手旁</td></tr><tr><td>d</td><td>丶、冫、氵、捺笔</td><td><strong>点</strong></td></tr><tr><td>o</td><td>日、月、目、曰</td><td>象形+归并</td></tr><tr><td>a</td><td><p>一、丨、乛、亅<br>（所有横、竖、折、弯笔）</p></td><td></td></tr><tr><td>p</td><td><p>丿<br>（所有丿）</p></td><td>撇</td></tr><tr><td>g</td><td><p>乚、勹<br>（所有勾）</p></td><td><strong>勾</strong></td></tr><tr><td>w</td><td>亠、夂、夊、攵</td><td><strong>文</strong>字头</td></tr><tr><td>b</td><td>宀、冖</td><td><strong>宝</strong>盖头、秃<strong>宝</strong>盖</td></tr><tr><td>q</td><td>䒑</td><td><strong>前</strong>字头</td></tr><tr><td>i</td><td>厂</td><td>「<strong>厂</strong>」的简化字</td></tr><tr><td>g</td><td>广</td><td>「<strong>广</strong>」的简化字</td></tr><tr><td>y</td><td>业</td><td>「<strong>业</strong>」的简化字</td></tr><tr><td>y</td><td>乁㇈</td><td>「<strong>乙</strong>」</td></tr></tbody></table><details class="details custom-block"><summary>练习：给出这些字的辅助码：飞、一、〇、厂、𠆭、𡆢</summary><ul><li>飞：yd 乙丶</li><li>一：aa 一一</li><li>〇：aa （曲笔）</li><li>厂：gi 广敞</li><li>𠆭：ra（曲笔）</li><li>𡆢：ka（曲笔）</li></ul></details><h2 id="字根编码" tabindex="-1">字根编码 <a class="header-anchor" href="#字根编码" aria-label="Permalink to &quot;字根编码&quot;">​</a></h2><p>可以使用 <code>pp</code> + 字根编码输入字根。</p><figure><img src="'+r+'" alt=""><figcaption></figcaption></figure><div class="info custom-block"><p class="custom-block-title">INFO</p><p>目前仅在<a href="./../usage/">默认模式</a>和<a href="./../schemas/zhengju/">整句模式</a>中可用。</p></div><h2 id="自然码形辅特点" tabindex="-1">自然码形辅特点 <a class="header-anchor" href="#自然码形辅特点" aria-label="Permalink to &quot;自然码形辅特点&quot;">​</a></h2><ul><li>学习成本极低</li><li>依据汉字本身的信息编码，作者主观干扰极少</li><li>传承字和简化字前三码往往一致，在不同字形间切换几乎没有成本</li></ul>',19)]))}const f=a(l,[["render",e]]);export{b as __pageData,f as default};
