import{_ as n,c as s,a4 as t,o as i}from"./chunks/framework.NOSrbWr_.js";const e="/assets/%E5%9B%BE%E7%89%87.D9PHBJIi.png",g=JSON.parse('{"title":"词库维护","description":"","frontmatter":{"title":"词库维护","order":4},"headers":[],"relativePath":"zh-Hans/book/maintenance/dict.md","filePath":"zh-Hans/book/maintenance/dict.md"}'),l={name:"zh-Hans/book/maintenance/dict.md"};function o(r,a,p,c,d,m){return i(),s("div",null,a[0]||(a[0]=[t(`<h1 id="词库维护" tabindex="-1">词库维护 <a class="header-anchor" href="#词库维护" aria-label="Permalink to &quot;词库维护&quot;">​</a></h1><h2 id="如何造词" tabindex="-1">如何造词？ <a class="header-anchor" href="#如何造词" aria-label="Permalink to &quot;如何造词？&quot;">​</a></h2><p>类整句模式（默认模式、整句模式、辅筛模式）下，任何连续输入的文本都会被添加到<strong>用户词库</strong>中。</p><p>字词模式下，默认情况下只能打词库和用户词库中有的词。要添加新词到<strong>用户词库</strong>中，可以使用<a href="./../schemas/zici/features.html#手动造词键">单引号快捷加词</a>。</p><div class="info custom-block"><p class="custom-block-title">INFO</p><p>类整句模式的用户词库保存在用户目录下的 moran.userdb 中。该目录是 LevelDB 数据库文件，不能直接编辑修改，但可以用 Rime 提供的词库管理程序导出或导入 txt。</p><p>字词模式的用户词库保存在用户目录下的 moran_fixed.txt 或 moran_fixed_simp.txt 中，可以直接使用文本编辑器查看和修改。</p></div><h2 id="如何把词语写入词库文件" tabindex="-1">如何把词语写入词库文件？ <a class="header-anchor" href="#如何把词语写入词库文件" aria-label="Permalink to &quot;如何把词语写入词库文件？&quot;">​</a></h2><p><strong>字词模式</strong>：编辑 moran_fixed.dict.yaml（传承字码表）或 moran_fixed_simp.dict.yaml（简化字码表）。</p><p><strong>整句模式</strong>：编辑 moran.extended.dict.yaml，</p><div class="language- vp-adaptive-theme"><button title="Copy Code" class="copy"></button><span class="lang"></span><pre class="shiki shiki-themes github-light github-dark vp-code" tabindex="0"><code><span class="line"><span># 用户自定义词库</span></span>
<span class="line"><span># ^ 在此提示之后，按 &lt;词&gt; &lt;tab&gt; &lt;编码&gt; 的格式一行一个词。</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 可以不加编码，Rime 会自动生成编码</span></span>
<span class="line"><span>魔然说明书</span></span>
<span class="line"><span></span></span>
<span class="line"><span># 编码可以任取，甚至字数和音节数不匹配也可以</span></span>
<span class="line"><span># 通过这种方式可以使得整句中可以混入「简码」连打</span></span>
<span class="line"><span>什么	sm</span></span>
<span class="line"><span>为什么	wsm</span></span></code></pre></div><h2 id="如何导入其他词库文件" tabindex="-1">如何导入其他词库文件？ <a class="header-anchor" href="#如何导入其他词库文件" aria-label="Permalink to &quot;如何导入其他词库文件？&quot;">​</a></h2><p>对于类整句方案（默认模式、整句模式、辅筛模式），编辑 moran.extended.dict.yaml，在 <code>import_tables</code> 下输入自己的词库名。</p><div class="warning custom-block"><p class="custom-block-title">WARNING</p><p>词库需是原生双拼词库。具体格式可参考 moran.base.dict.yaml。</p><p><strong>词语编码可以没有辅码</strong>，但这样的词语在默认模式和整句模式中不能加辅（辅筛模式不受影响）。</p><p>我们提供<a href="./../tools/schemagen/#命令-gen-dict">词库格式转换工具</a>。</p></div><p>作为一个用例，您可以把字词模式词库完全导入到整句词库中（虽然不建议这样的用法）：</p><figure><img src="`+e+'" alt=""><figcaption></figcaption></figure><h2 id="附录-有哪些词库文件" tabindex="-1">附录：有哪些词库文件？ <a class="header-anchor" href="#附录-有哪些词库文件" aria-label="Permalink to &quot;附录：有哪些词库文件？&quot;">​</a></h2><p>词库文件分为两类：</p><ul><li>整句词库，由类整句方案使用，包括<strong>默认模式</strong>、<strong>整句模式</strong>、<strong>辅筛模式</strong><ul><li>上述方案直接引用 moran.extended.dict.yaml，该文件中导入以下词库 <ul><li>moran.chars.dict.yaml 单字全码定义</li><li>moran.base.dict.yaml 核心词库</li><li>moran.words.dict.yaml 本方案扩充词库</li><li>moran.tencent.dict.yaml 腾讯词向量词库</li><li>moran.computer.dict.yaml 计算机术语词库</li><li>moran.moe.dict.yaml 萌娘百科词头</li><li>moran.hanyu.dict.yaml 汉语大词典（默认未开启）</li></ul></li></ul></li><li>字词和简码词库，由<strong>字词模式</strong>和<strong>默认模式</strong>使用 <ul><li>moran_fixed.dict.yaml 传承字码表</li><li>moran_fixed_simp.dict.yaml 简化字码表</li></ul></li></ul>',17)]))}const u=n(l,[["render",o]]);export{g as __pageData,u as default};
