import{_ as a,a as i,b as d,c as r,d as l,e,f as s,g as o,h,i as n}from"./chunks/image (82).oKHeAd98.js";import{_ as m,c,a4 as p,o as u}from"./chunks/framework.NOSrbWr_.js";const y=JSON.parse('{"title":"输入方法","description":"","frontmatter":{"title":"输入方法","order":2},"headers":[],"relativePath":"zh-Hans/book/schemas/fushai/usage.md","filePath":"zh-Hans/book/schemas/fushai/usage.md"}'),_={name:"zh-Hans/book/schemas/fushai/usage.md"};function g(b,t,f,z,k,x){return u(),c("div",null,t[0]||(t[0]=[p('<h1 id="输入方法" tabindex="-1">输入方法 <a class="header-anchor" href="#输入方法" aria-label="Permalink to &quot;输入方法&quot;">​</a></h1><h2 id="单字输入方法" tabindex="-1">单字输入方法 <a class="header-anchor" href="#单字输入方法" aria-label="Permalink to &quot;单字输入方法&quot;">​</a></h2><p>输入单字双拼后，直接选择候选项，或输入 1 位或 2 位辅助码。</p><ul><li>输入长度为 3 时，优先输出单字候选项。</li><li>输入长度为 4 时，单字首选项会与一起被调频。 <ul><li>例如，输入 doxx 时，若输入「多谢」次数比较多，则它可能出现在「多」的前面。</li></ul></li></ul><table data-view="cards"><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td></td><td><img src="'+a+'" alt="" data-size="original"></td><td>无辅助码</td></tr><tr><td></td><td><img src="'+i+'" alt="" data-size="original"></td><td>1 位辅助码</td></tr><tr><td></td><td><img src="'+d+'" alt="" data-size="original"></td><td>2 位辅助码</td></tr></tbody></table><h2 id="词语输入方法" tabindex="-1">词语输入方法 <a class="header-anchor" href="#词语输入方法" aria-label="Permalink to &quot;词语输入方法&quot;">​</a></h2><p>输入词语双拼后，可直接选择候选项，或输入 1 位或 2 位辅助码。</p><p>词语辅助码全码的定义是：</p><ol><li>首字的 2 位辅助码</li><li>末字的 2 位辅助码</li><li>末字的 1 位辅助码 + 首字的 1 位辅助码</li><li>首字的 1 位辅助码 + 末字的 1 位辅助码</li></ol><table data-view="cards"><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td><img src="'+r+'" alt="" data-size="original"></td><td></td><td>首字 2 码</td></tr><tr><td><img src="'+l+'" alt="" data-size="original"></td><td></td><td>末字 2 码</td></tr><tr><td><img src="'+e+'" alt="" data-size="original"></td><td></td><td>末 1 + 首 1</td></tr><tr><td><img src="'+s+'" alt="" data-size="original"></td><td></td><td>首 1 + 末 1</td></tr></tbody></table><h2 id="句子输入方法与造词方法" tabindex="-1">句子输入方法与造词方法 <a class="header-anchor" href="#句子输入方法与造词方法" aria-label="Permalink to &quot;句子输入方法与造词方法&quot;">​</a></h2><p>输入句子的完整全拼后，检视首选，如果首选不是想要输入的句子，则可输入 <strong>第一个词语或单字的</strong> 1 位 或 2 位辅助码。在输入完成后，该词语会进入用户词库，并在「魔然」和「魔然·整句」中也可以使用。</p><p>以输入和造词「辅筛模式」为例：</p><table data-view="cards"><thead><tr><th></th><th></th><th></th></tr></thead><tbody><tr><td><img src="'+o+'" alt="" data-size="original"></td><td>输入双拼</td><td></td></tr><tr><td><img src="'+h+'" alt="" data-size="original"></td><td></td><td>输入「辅」的辅助码 i</td></tr><tr><td><img src="'+n+'" alt="" data-size="original"></td><td>输入「筛」的辅助码 v</td><td></td></tr></tbody></table><ul><li>一开始直接输入「辅筛模式」的双拼 fu ul mo ui，观察到首选不是想要的词语。</li><li>输入 i ，即「辅」的辅助码，观察到「辅」字被提升到次选项，输入 2 选择该字。</li><li>输入 v，即「筛」的辅助码，观察到「筛」字被提升到次选项，输入 2 选择该字。</li><li>观察到首选是「模式」，空格确认。</li><li>上屏「辅筛模式」。</li><li>再次输入 fu ul mo ui，观察到首选是「辅筛模式」，说明该词语已经进入用户词库。</li></ul>',15)]))}const P=m(_,[["render",g]]);export{y as __pageData,P as default};
